import org.faktorips.values.Money;
import de.faktorips.schulung.hausratmodell.hausrat.HausratZusatzdeckung;
import org.faktorips.values.Decimal;
import java.math.RoundingMode;


public Money berechneJahresbasisbeitrag(HausratZusatzdeckung hausratZusatzdeckung)
{
return Money.valueOf("30.00EUR").add(hausratZusatzdeckung.getVersSumme().multiply(Decimal.valueOf("0.03"), RoundingMode.HALF_UP));
}
